,type='textfield'}H.filterTable.add{name='status_input',type='textfield'}H.filterTable.add{name='hours_input',type='textfield'}drawPlayerTable(q,H,false,false,{})end)addFrame('Admin',2,'Player List','Admin',"All admin fuctions are here")addButton('btn_toolbar_automessage',function()autoMessage()end)addButton('tp_all',function(q,H)for W,p in pairs(game.connected_players)do local ao=game.surfaces[q.surface.name].find_non_colliding_position("player",q.position,32,1)if p~=q then p.teleport(ao)end end end)addButton('revive_dead_entitys_range',function(q,H)if tonumber(H.parent.range.text)then local ap=tonumber(H.parent.range.text)for aq,g in pairs(game.surfaces[1].find_entities_filtered({area={{q.position.x-ap,q.position.y-ap},{q.position.x+ap,q.position.y+ap}},type="entity-ghost"}))do g.revive()end end end)addButton('add_dev_items',function(q,H)q.insert{name="deconstruction-planner",count=1}q.insert{name="blueprint-book",count=1}q.insert{name="blueprint",count=20}end)addButton('sendMessage',function(q,H)local rank=stringToRank(H.parent.message.rank.text)if rank then callRank(H.parent.message.message.text,rank.name)else for f,rank in pairs(ranks)do q.print(rank.name)end end end)addButton('setRanks',function(q,H)rank=stringToRank(H.parent.rank_input.text)if rank then for f,ar in pairs(selected[q.index])do p=game.players[ar]if getRank(q).power<getRank(p).power and rank.power>getRank(q).power then giveRank(p,rank,q)else q.print('You can not edit '..p.name.."'s rank there rank is too high (or the rank you have slected is above you)")end end else q.print(H.parent.rank_input.text..' is not a Rank, Ranks are:')for f,rank in pairs(ranks)do if rank.power>getRank(q).power then q.print(rank.name)end end end end)addButton('clearSelection',function(q,H)clearSelection(q)drawPlayerTable(q,H.parent.parent,false,true,{})end)addTab('Admin','Commands','Random useful commands',function(q,H)drawButton(H,'btn_toolbar_automessage','Auto Message','Send the auto message to all online players')drawButton(H,'add_dev_items','Get Blueprints','Get all the blueprints')drawButton(H,'revive_dead_entitys_range','Revive Entitys','Brings all dead machines back to life in player range')H.add{type='textfield',name='range',text='Range'}H.add{type='flow',name='message'}H.message.add{type='textfield',name='message',text='Enter message'}H.message.add{type='textfield',name='rank',text='Endter rank'}drawButton(H,'sendMessage','Send Message','Send a message to all ranks higher than the slected')drawButton(H,'tp_all','TP All Here','Brings all players to you')end)addTab('Admin','Edit Ranks','Edit the ranks of players below you',function(q,H)clearSelection(q)H.add{name='filterTable',type='table',colspan=2}H.filterTable.add{name='name_label',type='label',caption='Name'}H.filterTable.add{name='sel_label',type='label',caption='Selected?'}H.filterTable.add{name='name_input',type='textfield'}H.filterTable.add{name='sel_input',type='textfield'}H.add{type='flow',name='rank',direction='horizontal'}H.rank.add{name='rank_label',type='label',caption='Rank'}H.rank.add{name='rank_input',type='textfield'}drawButton(H.rank,'setRanks','Set Ranks','Sets the rank of all selected players')drawButton(H.rank,'clearSelection','Clear Selection','Clears all currently selected players')drawPlayerTable(q,H,false,true,{'lower'})end)addTab('Admin','Player List','Send player message to all players',function(q,H)H.add{name='filterTable',type='table',colspan=2}H.filterTable.add{name='name_label',type='label',caption='Name'}H.filterTable.add{name='hours_label',type='label',caption='Online Time (minutes)'}H.filterTable.add{name='name_input',type='textfield'}H.filterTable.add{name='hours_input',type='textfield'}drawPlayerTable(q,H,true,false,{'online'})end)addFrame('Admin+',1,'Modifiers','Admin+',"Because we are better")addButton('remove_biters',function(q,H)for aq,g in pairs(game.surfaces[1].find_entities_filtered({force='enemy'}))do g.destroy()end end)addButton('toggle_cheat',function(q,H)q.cheat_mode=not q.cheat_mode end)addButton('revive_dead_entitys',function(q,H)for aq,g in pairs(game.surfaces[1].find_entities_filtered({type="entity-ghost"}))do g.revive()end end)addButton("btn_Modifier_apply",function(q,H)local as={"manual_mining_speed_modifier","manual_crafting_speed_modifier","character_running_speed_modifier","worker_robots_speed_modifier","worker_robots_storage_bonus","character_build_distance_bonus","character_item_drop_distance_bonus","character_reach_distance_bonus","character_resource_reach_distance_bonus","character_item_pickup_distance_bonus","character_loot_pickup_distance_bonus"}for W,at in pairs(as)do local au=tonumber(H.parent.parent.modifierTable[at.."_input"].text:match("[%d]+[.%d+]"))if au~=nil then if au>=0 and au<50 and au~=q.force[at]then q.force[at]=au;q.print(at.." changed to number: "..tostring(au))elseif au==q.force[at]then q.print(at.." Did not change")else q.print(at.." needs to be player higher number or it contains an letter")end end end end)addTab('Admin+','Commands','Random useful commands',function(q,H)drawButton(H,'btn_toolbar_automessage','Auto Message','Send the auto message to all online players')drawButton(H,'add_dev_items','Get Blueprints','Get all the blueprints')drawButton(H,'revive_dead_entitys','Revive All Entitys','Brings all dead machines back to life')drawButton(H,'revive_dead_entitys_range','Revive Entitys','Brings all dead machines back to life in player range')H.add{type='textfield',name='range',text='Range'}drawButton(H,'remove_biters','Kill Biters','Removes all biters in map')drawButton(H,'tp_all','TP All Here','Brings all players to you')drawButton(H,'toggle_cheat','Toggle Cheat Mode','Toggle your cheat mode')end)addTab('Admin+','Modifiers','Edit in game modifiers',function(q,H)local as={"manual_mining_speed_modifier","manual_crafting_speed_modifier","character_running_speed_modifier","worker_robots_speed_modifier","worker_robots_storage_bonus","character_build_distance_bonus","character_item_drop_distance_bonus","character_reach_distance_bonus","character_resource_reach_distance_bonus","character_item_pickup_distance_bonus","character_loot_pickup_distance_bonus"}H.add{type="flow",name="flowNavigation",direction="horizontal"}H.add{name="modifierTable",type="table",colspan=3}H.modifierTable.add{name="name",type="label",caption="name"}H.modifierTable.add{name="input",type="label",caption="input"}H.modifierTable.add{name="current",type="label",caption="current"}for W,at in pairs(as)do H.modifierTable.add{name=at,type="label",caption=at}H.modifierTable.add{name=at.."_input",type="textfield",caption="inputTextField"}H.modifierTable.add{name=at.."_current",type="label",caption=tostring(q.force[at])}end;drawButton(H.flowNavigation,"btn_Modifier_apply","Apply","Apply the new values to the game")end)