e Entitys','Brings all dead machines back to life in player range')A.add{type='textfield',name='range',text='Range'}drawButton(A,'remove_biters','Kill Biters','Removes all biters in map')drawButton(A,'tp_all','TP All Here','Brings all players to you')drawButton(A,'toggle_cheat','Toggle Cheat Mode','Toggle your cheat mode')end)addTab('Admin','Modifiers','Edit in game modifiers',function(p,A)local aj={"manual_mining_speed_modifier","manual_crafting_speed_modifier","character_running_speed_modifier","worker_robots_speed_modifier","worker_robots_storage_bonus","character_build_distance_bonus","character_item_drop_distance_bonus","character_reach_distance_bonus","character_resource_reach_distance_bonus","character_item_pickup_distance_bonus","character_loot_pickup_distance_bonus"}A.add{type="flow",name="flowNavigation",direction="horizontal"}A.add{name="modifierTable",type="table",colspan=3}A.modifierTable.add{name="name",type="label",caption="name"}A.modifierTable.add{name="input",type="label",caption="input"}A.modifierTable.add{name="current",type="label",caption="current"}for P,ak in pairs(aj)do A.modifierTable.add{name=ak,type="label",caption=ak}A.modifierTable.add{name=ak.."_input",type="textfield",caption="inputTextField"}A.modifierTable.add{name=ak.."_current",type="label",caption=tostring(p.force[ak])}end;drawButton(A.flowNavigation,"btn_Modifier_apply","Apply","Apply the new values to the game")end)addTab('Admin','Player List','Send player message to all players',function(p,A)A.add{name='filterTable',type='table',colspan=2}A.filterTable.add{name='name_label',type='label',caption='Name'}A.filterTable.add{name='hours_label',type='label',caption='Online Time (minutes)'}A.filterTable.add{name='name_input',type='textfield'}A.filterTable.add{name='hours_input',type='textfield'}drawPlayerTable(p,A,true,{'online'})end)