d+]"))if ao~=nil then if ao>=0 and ao<50 and ao~=p.force[an]then p.force[an]=ao;p.print(an.." changed to number: "..tostring(ao))elseif ao==p.force[an]then p.print(an.." Did not change")else p.print(an.." needs to be player higher number or it contains an letter")end end end end)addTab('Admin','Commands','Random useful commands',function(p,F)drawButton(F,'btn_toolbar_automessage','Auto Message','Send the auto message to all online players')drawButton(F,'add_dev_items','Get Blueprints','Get all the blueprints')drawButton(F,'revive_dead_entitys','Revive All Entitys','Brings all dead machines back to life')drawButton(F,'revive_dead_entitys_range','Revive Entitys','Brings all dead machines back to life in player range')F.add{type='textfield',name='range',text='Range'}drawButton(F,'remove_biters','Kill Biters','Removes all biters in map')drawButton(F,'tp_all','TP All Here','Brings all players to you')drawButton(F,'toggle_cheat','Toggle Cheat Mode','Toggle your cheat mode')end)addTab('Admin','Modifiers','Edit in game modifiers',function(p,F)local am={"manual_mining_speed_modifier","manual_crafting_speed_modifier","character_running_speed_modifier","worker_robots_speed_modifier","worker_robots_storage_bonus","character_build_distance_bonus","character_item_drop_distance_bonus","character_reach_distance_bonus","character_resource_reach_distance_bonus","character_item_pickup_distance_bonus","character_loot_pickup_distance_bonus"}F.add{type="flow",name="flowNavigation",direction="horizontal"}F.add{name="modifierTable",type="table",colspan=3}F.modifierTable.add{name="name",type="label",caption="name"}F.modifierTable.add{name="input",type="label",caption="input"}F.modifierTable.add{name="current",type="label",caption="current"}for U,an in pairs(am)do F.modifierTable.add{name=an,type="label",caption=an}F.modifierTable.add{name=an.."_input",type="textfield",caption="inputTextField"}F.modifierTable.add{name=an.."_current",type="label",caption=tostring(p.force[an])}end;drawButton(F